mapper.select:
SELECT ci.* FROM cart_items ci
JOIN carts c ON ci.cart_id = c.id
WHERE c.user_id = #{userId}

mapper.insert:
INSERT INTO cart_items (cart_id, item_id, item_type, quantity)
SELECT c.id, #{itemId}, #{itemType}, #{quantity}
FROM carts c
WHERE c.user_id = #{userId}
ORDER BY c.id DESC
LIMIT 1

mapper.update:
UPDATE cart_items ci
JOIN carts c ON ci.cart_id = c.id

    SET ci.quantity = #{quantity}

WHERE c.user_id = #{userId} AND ci.item_id = #{itemId}

mapper.select:
SELECT ci.* FROM cart_items ci
JOIN carts c ON ci.cart_id = c.id
WHERE c.user_id = #{userId} AND ci.item_id = #{itemId}
